mapper.select:
SELECT counsel.id AS counselId, U.user_id AS userId, col.college AS college, dept.dept AS dept, U.student_code AS studentCode, C.year AS year, C.semester AS semester, C.class_no AS classNo, U.name AS name, counsel.counsel_datetime AS counselDatetime, counsel.counsel_contents AS counselContents, U2.name AS counselorName FROM pl_center.counsel AS counsel INNER JOIN pl_center.class AS C ON counsel.class_id = C.id INNER JOIN pl_center.users AS U ON counsel.user_code = U.user_code INNER JOIN pl_center.college AS col ON U.col_id = col.id INNER JOIN pl_center.dept AS dept ON U.dept_id = dept.id INNER JOIN pl_center.users AS U2 ON U2.user_code = counsel.counselor_user_code
WHERE U.user_code = ${userCode}
ORDER BY counsel.counsel_datetime DESC

SELECT counsel.id AS counselId, U.user_id AS userId, col.college AS college, dept.dept AS dept, U.student_code AS studentCode, C.year AS year, C.semester AS semester, C.class_no AS classNo, U.name AS name, counsel.counsel_datetime AS counselDatetime, counsel.counsel_contents AS counselContents, U2.name AS counselorName FROM pl_center.counsel AS counsel INNER JOIN pl_center.class AS C ON counsel.class_id = C.id INNER JOIN pl_center.users AS U ON counsel.user_code = U.user_code INNER JOIN pl_center.college AS col ON U.col_id = col.id INNER JOIN pl_center.dept AS dept ON U.dept_id = dept.id INNER JOIN pl_center.users AS U2 ON U2.user_code = counsel.counselor_user_code WHERE counsel.id IS NOT NULL
AND C.year = ${year}
AND C.semester = ${semester}
AND C.class_no = ${classNo}
AND ( U.user_id LIKE #{searchKey} OR col.college LIKE #{searchKey} OR dept.dept LIKE #{searchKey} OR U.student_code LIKE #{searchKey} OR U.name LIKE #{searchKey} )
ORDER BY counsel.counsel_datetime DESC

mapper.delete:
DELETE FROM counsel
WHERE id IN

