mapper.select:
SELECT id, title, U.name AS writer, write_datetime AS writeDatetime, views FROM notice AS N INNER JOIN users AS U ON N.writer = U.user_code
WHERE title LIKE #{searchKey} OR contents LIKE #{searchKey} OR U.name LIKE #{searchKey}
ORDER BY id DESC

mapper.update:
UPDATE notice
SET title = #{title}, contents = #{contents}
WHERE id = ${noticeId}
